insert  into `pueblo`(`id`,`nombre`,`img`,`informacion`,`status`,`municipio_id`) values (1,'Villa de Ahome','public/assets/Sinaloa/ahome/ahome-pueblo-senorial.png','{\"titulo\":\"Pueblo Señorial con más de 400 años de historia.\",\"descripcion\":\"Fundada en 1605, la Villa de Ahome tiene lugares y construcciones que la convierten en un sitio maravilloso, como la plazuela central, el parque recreativo, además de el Arco que da la bienvenida a los visitantes. Sinaloa de Leyva es reconocida en su gastronomía por su gran variedad de platillos autóctonos como quelites con queso, calabacitas en colachi, el huacabaqui, pozole, menudo, barbacoa, y los famosos cauques de la región.\"}',1,1),(2,'Concordia','public/assets/Sinaloa/concordia/concordia-pueblo-senorial.png','{\"titulo\":\"Se perfila como una localidad de lindos rincones..\",\"descripcion\":\"De arquitectura singular y bellos parajes naturales. Cuenta con aguas termales, flora y fauna endémica, cabañas y zonas arqueológicas que te encantarán. Es uno de los más nuevos pueblos señoriales de Sinaloa, título que se ganó gracias a su centro histórico que es rodeado por calles adoquinadas.\"}',1,5),(3,'Copala','public/assets/Sinaloa/concordia/copala-pueblo-senorial.png','{\"titulo\":\"Es un pueblo encantador que proyecta al México auténtico donde el tiempo se detuvo.\",\"descripcion\":\"En el ambiente tranquilo y acogedor de las calles de Copala se combinan la historia, la cultura y los bellos paisajes naturales, que causan fascinación entre los visitantes que recorren este antiguo pueblo minero enclavado en medio de las montañas de la Sierra Madre, a 71 kilómetros al este de Mazatlán. Es obligada una visita a la antigua iglesia ubicada en la plaza principal, así como un recorrido por las calles empedradas. En Copala podrá saborear el exquisito pay de plátano, muy tradicional en este lugar.\"}',1,5),(4,'Imala','public/assets/Sinaloa/culiacan/imala-pueblo-senorial.png','{\"titulo\":\"El aire colonial de siglos atrás parece mantenerse intacto en la hermosa Imala.\",\"descripcion\":\"El colorido y frescura de la naturaleza se aúna con las pequeñas viviendas tradicionales, coronadas con tejadillos rojos y frecuentemente decoradas con detalles de hierro forjado. En definitiva Imala es un pequeño pueblo con encanto, capaz de despertar la nostalgia y el recuerdo, e imbuir en un ambiente señorial al paseante que se adentra en sus calles.\"}',1,7),(5,'Elota','public/assets/Sinaloa/elota/elota-pueblo-senorial.png','{\"titulo\":\"Orgullosas raíces indígenas, danzas, artesanías y rica comida…\",\"descripcion\":\"Flanqueando las costas centrales del Estado, en un punto equidistante entre Mazatlán y Culiacán, emerge la hermosa población de Elota. El municipio cuenta con un extenso litoral de 45 Km. de longitud, contando con numerosas playas dotadas de los mejores servicios para hacer más placentera su visita. La Cruz es la cabeza del Municipio de Elota desde 1927. La Arquitectura predominante en La Cruz de Elota es moderna y prgmática, con edificios construidos mayoritariamente a lo largo del siglo XX.\"}',1,9),(6,'El Quelite','public/assets/Sinaloa/mazatlan/quelite-pueblo-senorial.png','{\"titulo\":\"El Encanto de la sencillez…\",\"descripcion\":\"El valor de la tradición apenas alterada tras el paso del tiempo, la delicia de pasear por unas calles marcadas por la cotidianeidad, son placeres que no todos los pueblos pueden ofrecer. El Quelite es la excepción; sin duda, uno  de los pueblos más píntorescos de Sinaloa.\"}',1,12),(7,'San Ignacio','public/assets/Sinaloa/san-ignacio/san-ignacio-pueblo-senorial.png','{\"titulo\":\"Las calles pintorescas encantan con edificaciones coloniales mostrando la historia de tesoros escondidos…\",\"descripcion\":\"En sus orígenes San Ignacio fue una misión jesuita fundada en el siglo XVI, hoy día es un hermoso lugar con magníficos ejemplos arquitectónicos de estética europea definidos por su marcado sabor colonial e histórico, sus calles lucen algunos monumentos dedicados a ilustres del pasado como a Don Benito Juárez o al General Gabriel Leyva Velázquez.\"}',1,17),(8,'Sinaloa de Leyva','public/assets/Sinaloa/sinaloa-de-leyva/sinaloa-leyva-pueblo-senorial.png','{\"titulo\":\"Sus calles estrechas y adoquinadas mantienen su aspecto de antaño, adornadas por casonas del siglo XIX…\",\"descripcion\":\"Los españoles bautizaron a este lugar como Villa de San Felipe y Santiago de Sinaloa, nombre que extendió a la actualidad. Éste fue el origen del patrimonio cultural de una pequeña ciudad serrana que convive con el pasado arquitectónico de sus edificios, con sus tradiciones que se aferran a perdurar y con un marco natural que sólo espera tu visita para enamorar. Sinaloa de Leyva es el punto de inicio de la evangelización cristiana del noroeste de México, con la llegada de los primeros misioneros jesuitas y llegó a ser la región más poblada de SInaloa gracias a sus esfuerzos. En la Actualidad, las calles estrechas y adoquinadas mantienen su aspecto de antaño, adornadas por varias casonas del siglo XIX, de arquitectura típicamente española.\"}',1,18),(9,'Choix','public/assets/Sinaloa/choix/choix-pueblo-senorial.png',NULL,1,4);

